sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
      return;
   }
	//g_form.addInfoMessage('Needit Populate Email Field execution start');
	var getEmailAddr = new GlideAjax('GetEmailAddress');
	getEmailAddr.addParam('sysparm_name','getEmail');
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));

	getEmailAddr.getXMLAnswer(populateEmailField);
	//getEmailAddr.getXMLAnswer(populateEmailField,null, )

	function populateEmailField(response){
		//g_form.addInfoMessage('in populateEmailField' );
		//g_form.addInfoMessage(response);
		var emailFromScriptInclude = response;
		//g_form.addInfoMessage(emailFromScriptInclude);
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email',emailFromScriptInclude);
	}

	//g_form.addInfoMessage('Needit Populate Email Field execution end');
   //Type appropriate comment here, and begin script below

}
